import{_ as t,p as e,q as d,a1 as l}from"./framework-96b046e1.js";const r={},n=l('<h1 id="_03-弹性盒子" tabindex="-1"><a class="header-anchor" href="#_03-弹性盒子" aria-hidden="true">#</a> 03 弹性盒子</h1><p><strong>介绍</strong></p><ol><li>容器默认存在两根轴：横轴和纵轴</li><li>采用flex布局的元素，称为flex容器，简称“容器”。他的所子元素成为flex项目。</li><li>父元素必须设置<code>display:flex</code>。</li></ol><p><strong>容器的属性</strong></p><blockquote><p>注意！！important!!</p><p>当将flex-direction设为column值时，align-items作用于横轴，justify-content作用于纵轴</p></blockquote><h2 id="_01-flex-direction" tabindex="-1"><a class="header-anchor" href="#_01-flex-direction" aria-hidden="true">#</a> 01 flex-direction</h2><blockquote><p>规定容器中的项目是按行排列还是按列排列</p></blockquote><table><thead><tr><th style="text-align:center;">序号</th><th>属性值</th><th>说明</th></tr></thead><tbody><tr><td style="text-align:center;">1</td><td><code>row</code></td><td>默认值。按行排列，起点在左端</td></tr><tr><td style="text-align:center;">2</td><td><code>row-reverse</code></td><td>按行排列，起点在右端</td></tr><tr><td style="text-align:center;">3</td><td><code>column</code></td><td>按列排列，起点在上端</td></tr><tr><td style="text-align:center;">4</td><td><code>column-reverse</code></td><td>按列排列，七点在下端</td></tr></tbody></table><h2 id="_02-justify-content" tabindex="-1"><a class="header-anchor" href="#_02-justify-content" aria-hidden="true">#</a> 02 justify-content</h2><blockquote><p>定义了项目在“横轴”上的对齐方式（横轴是相对概念）</p></blockquote><table><thead><tr><th style="text-align:center;">序号</th><th style="text-align:center;">属性值</th><th style="text-align:center;">说明</th></tr></thead><tbody><tr><td style="text-align:center;">1</td><td style="text-align:center;"><code>flex-start</code></td><td style="text-align:center;">默认值，左对齐，从左向右依次排列</td></tr><tr><td style="text-align:center;">2</td><td style="text-align:center;"><code>flex-end</code></td><td style="text-align:center;">右对齐，从右向左依次排列</td></tr><tr><td style="text-align:center;">3</td><td style="text-align:center;"><code>center</code></td><td style="text-align:center;">剧中</td></tr><tr><td style="text-align:center;">4</td><td style="text-align:center;"><code>space-around</code></td><td style="text-align:center;">项目之间和两侧都有间隔</td></tr><tr><td style="text-align:center;">5</td><td style="text-align:center;"><code>space-between</code></td><td style="text-align:center;">两端对齐（左右都不留间隙）</td></tr></tbody></table><h2 id="_03-align-items" tabindex="-1"><a class="header-anchor" href="#_03-align-items" aria-hidden="true">#</a> 03 align-items</h2><blockquote><p>定义了项目在“纵轴”上的对齐方式（纵轴是相对概念）</p></blockquote><table><thead><tr><th style="text-align:center;">序号</th><th style="text-align:center;">取值</th><th style="text-align:center;">说明</th></tr></thead><tbody><tr><td style="text-align:center;">1</td><td style="text-align:center;"><code>flex-start</code></td><td style="text-align:center;">从纵轴的最上方起点处排列对齐</td></tr><tr><td style="text-align:center;">2</td><td style="text-align:center;"><code>flex-end</code></td><td style="text-align:center;">从纵轴的最下方终点处排列对齐</td></tr><tr><td style="text-align:center;">3</td><td style="text-align:center;"><code>center</code></td><td style="text-align:center;">以纵轴的中心对齐</td></tr><tr><td style="text-align:center;">4</td><td style="text-align:center;"><code>baseline</code></td><td style="text-align:center;">所有项目的第一行文字的基线（理解为下划线）对齐</td></tr><tr><td style="text-align:center;">5</td><td style="text-align:center;"><code>stretch</code></td><td style="text-align:center;">默认值，如果项目未设置高度或者设为auto，每个项目将占满整个容器</td></tr></tbody></table><h2 id="_04-flex-wrap" tabindex="-1"><a class="header-anchor" href="#_04-flex-wrap" aria-hidden="true">#</a> 04 flex-wrap</h2><blockquote><p>规定项目排列是否换行</p></blockquote><table><thead><tr><th>序号</th><th>取值</th><th>说明</th></tr></thead><tbody><tr><td>1</td><td><strong><code>nowrap</code></strong></td><td>默认值，不换行，所有项目排在第一行</td></tr><tr><td>2</td><td><code>wrap</code></td><td>换行，第一行在最上方</td></tr><tr><td>3</td><td><code>wrap</code></td><td>换行，第一行在最下方</td></tr></tbody></table><h2 id="_05-flex-flow" tabindex="-1"><a class="header-anchor" href="#_05-flex-flow" aria-hidden="true">#</a> 05 flex-flow</h2><blockquote><p><code>flex-flow：flex-direction flex-wrap</code></p></blockquote>',19),a=[n];function c(o,i){return e(),d("div",null,a)}const h=t(r,[["render",c],["__file","03弹性盒子.html.vue"]]);export{h as default};
