import{_ as i,p as l,q as t,a1 as e}from"./framework-96b046e1.js";const s="/frontEnd/assets/7438f56cd1defb65dc98f1b4b261bf1-42462f97.jpg",a={},c=e('<h1 id="_01-快捷键" tabindex="-1"><a class="header-anchor" href="#_01-快捷键" aria-hidden="true">#</a> 01 快捷键</h1><p>鼠标中键：旋转镜头。 shift + 鼠标中键：移动镜头。</p><ol><li>delete删除</li><li>x删除</li><li>g移动，g后x、y、z沿着不同轴移动。g就是go，实际是glide滑行。</li><li>s缩放，s后x、y、z也可。鼠标右键退出缩放。</li><li>r旋转，默认以垂直视图方向旋转。x、y、z也可。</li><li>alt + s撤销缩放。</li><li>alt + r 撤销旋转。</li><li>alt + g 撤销平移。</li><li>h隐藏。</li><li>alt + h 显示全部隐藏的物体。</li><li>shift + h 把没有被选中的物体隐藏。</li><li>shift + d 移动并复制。</li><li>1 正视图</li><li>3 侧视图</li><li>7 顶视图</li><li>9 把当前视图反过来</li><li>2、4、6、8 上下左右微调，按一次转动15度。</li><li>5 切换正交视图和透视视图。</li><li>0 切换摄像机视角度。</li><li>. 移动到面前。</li><li>~ （esc下面的那个按键） 显示各个视图的选择</li><li>alt + 鼠标中键 也可以切换视图。</li><li>+、- 缩放微调。</li><li>ctrl + 空格 全屏。</li><li>shift + 右键 游标定位。游标控制新建物体的位置。</li><li>选中物体 shift + s 出现游标相关的选项。</li><li>shift + c 游标回到正中间。</li><li>g z是沿着z轴移动（g z z可切换坐标全局和局部）</li><li>/ 只看当前物体。</li><li>F12 渲染</li><li>shift多选物体，ctrl + L关联材质，即可复制材质。</li><li>N 可以在摄像机模式下锁定摄像机</li></ol><p>还有别的，文字与图片互补。 <img src="'+s+'" alt="22"></p>',4),r=[c];function _(o,f){return l(),t("div",null,r)}const d=i(a,[["render",_],["__file","01快捷键.html.vue"]]);export{d as default};
