import{_ as t,p as e,q as d,a1 as a}from"./framework-96b046e1.js";const h={},r=a('<h1 id="_09-创建表格" tabindex="-1"><a class="header-anchor" href="#_09-创建表格" aria-hidden="true">#</a> 09 创建表格</h1><p>一个简单的表格是这么创建的：</p><table><thead><tr><th>项目</th><th>Value</th></tr></thead><tbody><tr><td>电脑</td><td>$1600</td></tr><tr><td>手机</td><td>$12</td></tr><tr><td>导管</td><td>$1</td></tr></tbody></table><h2 id="_1-设定内容居中、居左、居右" tabindex="-1"><a class="header-anchor" href="#_1-设定内容居中、居左、居右" aria-hidden="true">#</a> 1 设定内容居中、居左、居右</h2><p>使用<code>:---------:</code>居中 使用<code>:----------</code>居左 使用<code>----------:</code>居右</p><table><thead><tr><th style="text-align:center;">第一列</th><th style="text-align:right;">第二列</th><th style="text-align:left;">第三列</th></tr></thead><tbody><tr><td style="text-align:center;">第一列文本居中</td><td style="text-align:right;">第二列文本居右</td><td style="text-align:left;">第三列文本居左</td></tr></tbody></table><h2 id="_2-smartypants" tabindex="-1"><a class="header-anchor" href="#_2-smartypants" aria-hidden="true">#</a> 2 SmartyPants</h2><p>SmartyPants将ASCII标点字符转换为“智能”印刷标点HTML实体。例如：</p><table><thead><tr><th>TYPE</th><th>ASCII</th><th>HTML</th></tr></thead><tbody><tr><td>Single backticks</td><td><code>&#39;Isn&#39;t this fun?&#39;</code></td><td>&#39;Isn&#39;t this fun?&#39;</td></tr><tr><td>Quotes</td><td><code>&quot;Isn&#39;t this fun?&quot;</code></td><td>&quot;Isn&#39;t this fun?&quot;</td></tr><tr><td>Dashes</td><td><code>-- is en-dash, --- is em-dash</code></td><td>-- is en-dash, --- is em-dash</td></tr></tbody></table>',9),s=[r];function n(o,c){return e(),d("div",null,s)}const l=t(h,[["render",n],["__file","09创建表格.html.vue"]]);export{l as default};
